SELECT MAX("coaches"."stint" + "draft"."tmID") as agg_0, MAX("teams"."d_fgm" + "teams"."games") as agg_1, SUM("players_teams"."PostRebounds") as agg_2 FROM "draft" LEFT OUTER JOIN "teams" ON "draft"."tmID" = "teams"."tmID" LEFT OUTER JOIN "series_post" ON "teams"."tmID" = "series_post"."tmIDLoser" LEFT OUTER JOIN "coaches" ON "teams"."tmID" = "coaches"."tmID" LEFT OUTER JOIN "players_teams" ON "teams"."tmID" = "players_teams"."tmID"  WHERE "coaches"."coachID" LIKE '%fitzsco01%' AND "series_post"."lgIDLoser" != 'NBA' AND "coaches"."lgID" IN ('PBLA', 'NBA', 'ABA', 'ABL1') AND "players_teams"."lgID" IN ('NBL', 'ABL1', 'PBLA', 'NPBL', 'NBA') AND "draft"."firstName" != 'Kareem'