SELECT AVG("coaches"."tmID" + "series_post"."year") as agg_0 FROM "players_teams" LEFT OUTER JOIN "teams" ON "players_teams"."tmID" = "teams"."tmID" LEFT OUTER JOIN "series_post" ON "teams"."tmID" = "series_post"."tmIDLoser" LEFT OUTER JOIN "coaches" ON "teams"."tmID" = "coaches"."tmID" LEFT OUTER JOIN "draft" ON "teams"."tmID" = "draft"."tmID"  WHERE "teams"."lgID" NOT LIKE '%NBA%' AND "coaches"."coachID" != 'macaued01'