SELECT MIN("players_teams"."threeMade" + "players_teams"."assists") as agg_0 FROM "players" LEFT OUTER JOIN "player_allstar" ON "players"."playerID" = "player_allstar"."playerID" LEFT OUTER JOIN "awards_players" ON "players"."playerID" = "awards_players"."playerID" LEFT OUTER JOIN "players_teams" ON "players"."playerID" = "players_teams"."playerID"  WHERE ("player_allstar"."first_name" = 'Tim' OR ("player_allstar"."d_rebounds" BETWEEN 0.4301481133527907 AND 0.9910392675486863 AND "player_allstar"."first_name" LIKE '%Geo%rge%')) AND "awards_players"."year" >= 2004 AND "awards_players"."award" != 'Comeback'