SELECT SUM("awards_players"."year") as agg_0, AVG("awards_players"."year") as agg_1 FROM "awards_players"  WHERE ("awards_players"."pos" != 'G' OR "awards_players"."year" <= 2002) AND "awards_players"."award" IN ('All-ABA First Team', 'All-NBA Second Team', 'All-Defensive First Team', 'All-ABA Second Team', 'All-Defensive Second Team', 'Most Valuable Player', 'Rookie of the Year', 'All-NBA Third Team', 'All-NBA First Team') AND "awards_players"."lgID" LIKE '%ABA%' AND "awards_players"."note" = 'tie' AND "awards_players"."playerID" >= 152015