SELECT MIN("players_teams"."minutes" + "teams"."d_fta") as agg_0, AVG("teams"."homeLost" + "teams"."o_tmRebound") as agg_1 FROM "players_teams" LEFT OUTER JOIN "players" ON "players_teams"."playerID" = "players"."playerID" LEFT OUTER JOIN "awards_players" ON "players"."playerID" = "awards_players"."playerID" LEFT OUTER JOIN "teams" ON "players_teams"."tmID" = "teams"."tmID" LEFT OUTER JOIN "draft" ON "teams"."tmID" = "draft"."tmID"  WHERE "players_teams"."PostftMade" BETWEEN 0 AND 1 AND "teams"."name" = 'Capital Bullets' AND "players"."deathDate" LIKE '%0000-00-00%' AND "draft"."lastName" LIKE '%Johnson%' AND "teams"."confID" IN ('WC') AND "players"."birthCity" LIKE '%Angele%s%'