SELECT MIN("players_teams"."threeAttempted" + "players_teams"."dRebounds") as agg_0 FROM "awards_players" LEFT OUTER JOIN "players" ON "awards_players"."playerID" = "players"."playerID" LEFT OUTER JOIN "players_teams" ON "players"."playerID" = "players_teams"."playerID"  WHERE "players_teams"."threeAttempted" >= 132 AND "players"."highSchool" != 'La Mirada' AND "awards_players"."lgID" = 'ABL1' AND "players"."race" = 'r'