SELECT SUM("teams"."d_ftm") as agg_0, MIN("teams"."d_ftm") as agg_1, SUM("teams"."d_3pm" + "coaches"."post_losses") as agg_2 FROM "series_post" LEFT OUTER JOIN "teams" ON "series_post"."tmIDWinner" = "teams"."tmID" LEFT OUTER JOIN "coaches" ON "teams"."tmID" = "coaches"."tmID"  WHERE "series_post"."L" BETWEEN 1 AND 3 AND "coaches"."lost" BETWEEN 40.49763131929734 AND 41.606471831551914 AND "coaches"."coachID" NOT LIKE '%wilkele01%'