SELECT MIN("teams"."games" + "series_post"."tmIDWinner") as agg_0, SUM("teams"."d_pts" + "teams"."o_tmRebound") as agg_1, MIN("coaches"."post_wins") as agg_2 FROM "series_post" LEFT OUTER JOIN "teams" ON "series_post"."tmIDWinner" = "teams"."tmID" LEFT OUTER JOIN "draft" ON "teams"."tmID" = "draft"."tmID" LEFT OUTER JOIN "coaches" ON "teams"."tmID" = "coaches"."tmID"  WHERE "draft"."draftOverall" <= 1 AND "teams"."franchID" != 'PIT' AND "series_post"."tmIDWinner" <= 6326 AND "draft"."firstName" != 'Mel'