SELECT MIN("coaches"."post_wins" + "coaches"."tmID") as agg_0, SUM("coaches"."year") as agg_1, SUM("coaches"."year" + "coaches"."post_wins") as agg_2 FROM "coaches"  WHERE "coaches"."coachID" IN ('sloanje01') AND "coaches"."lgID" NOT LIKE '%AB%L1%' AND "coaches"."coachID" = 'borylvi01' AND ("coaches"."stint" != 1 OR "coaches"."stint" BETWEEN 1 AND 2 OR "coaches"."stint" BETWEEN 1 AND 2) AND "coaches"."won" >= 47.4301192692071