SELECT MIN("teams"."awayWon" + "players_teams"."PostftAttempted") as agg_0 FROM "teams" LEFT OUTER JOIN "players_teams" ON "teams"."tmID" = "players_teams"."tmID"  WHERE "teams"."playoff" NOT LIKE '%C%1%' AND "players_teams"."lgID" IN ('ABL1', 'ABA', 'NPBL') AND "teams"."name" IN ('Indiana Pacers', 'Philadelphia 76ers', 'Detroit Pistons', 'Chica Bulls', 'Los Angeles Lakers', 'Phoenix Suns') AND "players_teams"."PostGP" >= 0 AND "teams"."confWon" >= 21