SELECT AVG("teams"."d_oreb" + "teams"."awayLost") as agg_0, MAX("teams"."divWon") as agg_1 FROM "awards_coaches" LEFT OUTER JOIN "coaches" ON "awards_coaches"."coachID" = "coaches"."coachID" LEFT OUTER JOIN "teams" ON "coaches"."tmID" = "teams"."tmID" LEFT OUTER JOIN "series_post" ON "teams"."tmID" = "series_post"."tmIDWinner"  WHERE ("coaches"."lgID" IN ('PBLA', 'NBA', 'ABA', 'NPBL') OR "coaches"."lgID" NOT LIKE '%AB%A%') AND "teams"."d_pts" >= 6039 AND "awards_coaches"."note" NOT LIKE '%t%ie%' AND "awards_coaches"."id" BETWEEN 14 AND 54 AND "awards_coaches"."coachID" NOT LIKE '%schay%do01%' AND "teams"."name" != 'Chica Packers'