SELECT MIN("teams"."d_3pa") as agg_0, COUNT(*) as agg_1 FROM "series_post" LEFT OUTER JOIN "teams" ON "series_post"."tmIDLoser" = "teams"."tmID" LEFT OUTER JOIN "draft" ON "teams"."tmID" = "draft"."tmID" LEFT OUTER JOIN "players_teams" ON "teams"."tmID" = "players_teams"."tmID"  WHERE "teams"."d_pts" <= 8985 AND "players_teams"."ftMade" <= 50 AND "draft"."draftFrom" NOT LIKE '%South%' AND "teams"."divID" != 'WE' AND ("players_teams"."lgID" NOT LIKE '%N%BA%' OR "players_teams"."lgID" LIKE '%N%BA%' OR "players_teams"."lgID" != 'NBL')