SELECT MAX("players_teams"."PostthreeAttempted") as agg_0, COUNT(*) as agg_1, MIN("teams"."o_to" + "series_post"."id") as agg_2 FROM "awards_coaches" LEFT OUTER JOIN "coaches" ON "awards_coaches"."coachID" = "coaches"."coachID" LEFT OUTER JOIN "teams" ON "coaches"."tmID" = "teams"."tmID" LEFT OUTER JOIN "series_post" ON "teams"."tmID" = "series_post"."tmIDWinner" LEFT OUTER JOIN "players_teams" ON "teams"."tmID" = "players_teams"."tmID"  WHERE "teams"."lgID" IN ('NBL') AND "awards_coaches"."id" BETWEEN 3 AND 21 AND ("coaches"."coachID" != 'inmanst01' OR "coaches"."coachID" LIKE '%ma%clejo01%') AND "players_teams"."lgID" LIKE '%NBA%'