SELECT COUNT(*) as agg_0, MAX("players_teams"."blocks" + "players_teams"."rebounds") as agg_1 FROM "players_teams"  WHERE "players_teams"."steals" >= 30 AND "players_teams"."minutes" <= 1537 AND "players_teams"."lgID" NOT LIKE '%NBA%' AND ("players_teams"."PostGP" >= 22 OR ("players_teams"."PostGP" BETWEEN 0 AND 10 AND "players_teams"."PostGP" != 0)) AND "players_teams"."GP" >= 69