SELECT AVG("teams"."o_fta" + "players_teams"."blocks") as agg_0, MAX("players_teams"."stint") as agg_1, MAX("players_teams"."PostGS" + "teams"."o_dreb") as agg_2 FROM "players" LEFT OUTER JOIN "players_teams" ON "players"."playerID" = "players_teams"."playerID" LEFT OUTER JOIN "player_allstar" ON "players"."playerID" = "player_allstar"."playerID" LEFT OUTER JOIN "teams" ON "players_teams"."tmID" = "teams"."tmID"  WHERE ("players"."highSchool" IS NOT NULL OR ("players"."highSchool" LIKE '%Washington%' AND "players"."highSchool" IN ('Jefferson', 'Washington', 'East', 'Thomas Jefferson', 'Booker T. Washington'))) AND "players"."birthCountry" IN ('GER', 'AUS', 'USA', 'CAN', 'BRA', 'ARG', 'ESP', 'CRO') AND "player_allstar"."last_name" NOT LIKE '%Jones%'