SELECT SUM("teams"."d_reb" + "teams"."confLoss") as agg_0 FROM "awards_players" LEFT OUTER JOIN "players" ON "awards_players"."playerID" = "players"."playerID" LEFT OUTER JOIN "players_teams" ON "players"."playerID" = "players_teams"."playerID" LEFT OUTER JOIN "teams" ON "players_teams"."tmID" = "teams"."tmID" LEFT OUTER JOIN "player_allstar" ON "players"."playerID" = "player_allstar"."playerID"  WHERE "player_allstar"."conference" LIKE '%Ea%st%' AND "awards_players"."award" = 'All-Defensive Second Team' AND ("players_teams"."lgID" NOT LIKE '%ABA%' OR ("players_teams"."lgID" IN ('ABL1', 'NBL', 'PBLA', 'NBA', 'NPBL') AND "players_teams"."lgID" = 'ABL1'))