SELECT COUNT(*) as agg_0, MAX("teams"."o_oreb") as agg_1 FROM "players_teams" LEFT OUTER JOIN "players" ON "players_teams"."playerID" = "players"."playerID" LEFT OUTER JOIN "teams" ON "players_teams"."tmID" = "teams"."tmID" LEFT OUTER JOIN "series_post" ON "teams"."tmID" = "series_post"."tmIDLoser" LEFT OUTER JOIN "draft" ON "teams"."tmID" = "draft"."tmID"  WHERE "players"."collegeOther" IN ('Indiana', 'Minnesota', 'San Jacinto JC', 'Illinois', 'Kentucky') AND "players_teams"."lgID" LIKE '%NB%A%'