SELECT MIN("teams"."confRank") as agg_0 FROM "coaches" LEFT OUTER JOIN "awards_coaches" ON "coaches"."coachID" = "awards_coaches"."coachID" LEFT OUTER JOIN "teams" ON "coaches"."tmID" = "teams"."tmID"  WHERE "coaches"."post_wins" >= 1.8985591845722682 AND "awards_coaches"."lgID" LIKE '%NB%A%' AND ("coaches"."lgID" LIKE '%ABA%' OR ("coaches"."post_losses" <= 2.9064996655712623 AND "coaches"."lgID" = 'NPBL')) AND "teams"."games" >= 82 AND "awards_coaches"."coachID" NOT LIKE '%jacksph01%'