SELECT AVG("players_teams"."fgMade" + "players_teams"."ftAttempted") as agg_0, SUM("teams"."o_pf") as agg_1, COUNT(*) as agg_2 FROM "draft" LEFT OUTER JOIN "teams" ON "draft"."tmID" = "teams"."tmID" LEFT OUTER JOIN "players_teams" ON "teams"."tmID" = "players_teams"."tmID" LEFT OUTER JOIN "series_post" ON "teams"."tmID" = "series_post"."tmIDLoser"  WHERE "draft"."tmID" >= 3022 OR ("draft"."tmID" >= 15428 AND "draft"."tmID" >= 4692)