SELECT MIN("players_teams"."PostdRebounds") as agg_0 FROM "players" LEFT OUTER JOIN "player_allstar" ON "players"."playerID" = "player_allstar"."playerID" LEFT OUTER JOIN "players_teams" ON "players"."playerID" = "players_teams"."playerID" LEFT OUTER JOIN "awards_players" ON "players"."playerID" = "awards_players"."playerID"  WHERE "players_teams"."lgID" LIKE '%NBA%' AND "players"."firstName" != 'Garfield' AND "player_allstar"."season_id" >= 1969 AND "player_allstar"."games_played" BETWEEN 1 AND 2 AND "players_teams"."PostftMade" <= 0