SELECT MAX("teams"."won") as agg_0, MIN("teams"."o_fgm" + "teams"."d_dreb") as agg_1 FROM "draft" LEFT OUTER JOIN "teams" ON "draft"."tmID" = "teams"."tmID" LEFT OUTER JOIN "series_post" ON "teams"."tmID" = "series_post"."tmIDWinner" LEFT OUTER JOIN "coaches" ON "teams"."tmID" = "coaches"."tmID"  WHERE "coaches"."won" >= 43.584737495701205 AND "teams"."attendance" <= 32767 AND ("series_post"."lgIDLoser" IN ('NBA') OR "series_post"."year" != 1979 OR "series_post"."lgIDLoser" IN ('ABA')) AND "coaches"."lgID" NOT LIKE '%A%BA%' AND "coaches"."coachID" NOT LIKE '%rile%ypa01%'