SELECT AVG("teams"."homeLost" + "teams"."o_stl") as agg_0, MAX("players_teams"."dRebounds" + "teams"."o_ftm") as agg_1 FROM "players_teams" LEFT OUTER JOIN "players" ON "players_teams"."playerID" = "players"."playerID" LEFT OUTER JOIN "teams" ON "players_teams"."tmID" = "teams"."tmID" LEFT OUTER JOIN "series_post" ON "teams"."tmID" = "series_post"."tmIDLoser"  WHERE "players_teams"."PostdRebounds" BETWEEN 0 AND 1 AND ("players"."deathDate" LIKE '%00%00-00-00%' OR "players"."useFirst" LIKE '%Bo%b%') AND "teams"."confID" IN ('EC') AND "players"."firstName" NOT LIKE '%Robert%'