SELECT COUNT(*) as agg_0, MIN("players"."playerID" + "player_allstar"."season_id") as agg_1, COUNT(*) as agg_2 FROM "players" LEFT OUTER JOIN "player_allstar" ON "players"."playerID" = "player_allstar"."playerID" LEFT OUTER JOIN "awards_players" ON "players"."playerID" = "awards_players"."playerID"  WHERE "players"."highSchool" NOT LIKE '%St.%' AND "player_allstar"."ft_attempted" <= 0.0 AND "player_allstar"."league_id" LIKE '%AB%A%' AND "player_allstar"."last_name" LIKE '%Malone%'