SELECT SUM("coaches"."stint" + "players_teams"."minutes") as agg_0, MAX("teams"."d_fgm") as agg_1 FROM "players_teams" LEFT OUTER JOIN "teams" ON "players_teams"."tmID" = "teams"."tmID" LEFT OUTER JOIN "coaches" ON "teams"."tmID" = "coaches"."tmID" LEFT OUTER JOIN "players" ON "players_teams"."playerID" = "players"."playerID" LEFT OUTER JOIN "awards_coaches" ON "coaches"."coachID" = "awards_coaches"."coachID"  WHERE "players"."lastName" = 'Maury'