SELECT AVG("players_teams"."dRebounds") as agg_0 FROM "players_teams" LEFT OUTER JOIN "players" ON "players_teams"."playerID" = "players"."playerID" LEFT OUTER JOIN "player_allstar" ON "players"."playerID" = "player_allstar"."playerID" LEFT OUTER JOIN "awards_players" ON "players"."playerID" = "awards_players"."playerID"  WHERE "players"."useFirst" != 'Pep' AND "awards_players"."pos" IN ('G')