SELECT MIN("coaches"."tmID" + "coaches"."post_wins") as agg_0, MAX("coaches"."post_losses") as agg_1, COUNT(*) as agg_2 FROM "awards_coaches" LEFT OUTER JOIN "coaches" ON "awards_coaches"."coachID" = "coaches"."coachID"  WHERE "coaches"."won" BETWEEN 2.551164392324041 AND 13.53305923951573 AND "coaches"."coachID" != 'pettibo01' AND "awards_coaches"."coachID" != 'mottadi01' AND "coaches"."lgID" != 'NBA' AND "awards_coaches"."note" NOT LIKE '%tie%'