SELECT MIN("players_teams"."stint") as agg_0 FROM "coaches" LEFT OUTER JOIN "awards_coaches" ON "coaches"."coachID" = "awards_coaches"."coachID" LEFT OUTER JOIN "teams" ON "coaches"."tmID" = "teams"."tmID" LEFT OUTER JOIN "series_post" ON "teams"."tmID" = "series_post"."tmIDWinner" LEFT OUTER JOIN "players_teams" ON "teams"."tmID" = "players_teams"."tmID"  WHERE ("players_teams"."assists" <= 265 OR ("players_teams"."assists" >= 5 AND "players_teams"."assists" >= 146)) AND "players_teams"."threeAttempted" <= 24 AND "series_post"."lgIDWinner" LIKE '%ABA%' AND "coaches"."stint" != 1 AND "awards_coaches"."coachID" IN ('fitchbi01', 'rileypa01')