SELECT AVG("teams"."o_oreb") as agg_0 FROM "players_teams" LEFT OUTER JOIN "teams" ON "players_teams"."tmID" = "teams"."tmID" LEFT OUTER JOIN "coaches" ON "teams"."tmID" = "coaches"."tmID" LEFT OUTER JOIN "draft" ON "teams"."tmID" = "draft"."tmID"  WHERE "coaches"."tmID" <= 4595 AND ("draft"."lgID" NOT LIKE '%NB%A%' OR "draft"."tmID" <= 1813 OR "draft"."lgID" IN ('NBA')) AND "players_teams"."playerID" >= 491575 AND ("players_teams"."lgID" IN ('NBA', 'NBL', 'ABA', 'NPBL') OR "players_teams"."threeAttempted" <= 0 OR "players_teams"."lgID" NOT LIKE '%NB%L%') AND "teams"."confID" IN ('WC') AND "draft"."lastName" != 'Rucker'