SELECT AVG("players_teams"."PostftAttempted") as agg_0, MAX("players"."firstseason") as agg_1, COUNT(*) as agg_2 FROM "awards_players" LEFT OUTER JOIN "players" ON "awards_players"."playerID" = "players"."playerID" LEFT OUTER JOIN "players_teams" ON "players"."playerID" = "players_teams"."playerID" LEFT OUTER JOIN "teams" ON "players_teams"."tmID" = "teams"."tmID"  WHERE "players"."lastName" != 'Poquette'