SELECT MIN("teams"."neutWon" + "teams"."games") as agg_0, COUNT(*) as agg_1 FROM "series_post" LEFT OUTER JOIN "teams" ON "series_post"."tmIDLoser" = "teams"."tmID" LEFT OUTER JOIN "draft" ON "teams"."tmID" = "draft"."tmID" LEFT OUTER JOIN "players_teams" ON "teams"."tmID" = "players_teams"."tmID"  WHERE "players_teams"."GS" = 41 AND "draft"."draftYear" = 1988 AND "teams"."neutLoss" BETWEEN 0 AND 1 AND "teams"."d_tmRebound" <= 0 AND "teams"."o_reb" >= 3766 AND "players_teams"."lgID" NOT LIKE '%N%BL%'