SELECT MAX("teams"."d_stl") as agg_0, MIN("teams"."o_tmRebound" + "players_teams"."threeAttempted") as agg_1 FROM "teams" LEFT OUTER JOIN "players_teams" ON "teams"."tmID" = "players_teams"."tmID" LEFT OUTER JOIN "series_post" ON "teams"."tmID" = "series_post"."tmIDLoser" LEFT OUTER JOIN "draft" ON "teams"."tmID" = "draft"."tmID"  WHERE "players_teams"."lgID" != 'ABA' AND "series_post"."lgIDWinner" != 'ABA' AND "series_post"."tmIDWinner" <= 9720 AND "players_teams"."PostMinutes" BETWEEN 0 AND 157