SELECT MAX("teams"."lost" + "teams"."o_pf") as agg_0, MAX("players_teams"."stint") as agg_1, AVG("teams"."min" + "teams"."d_oreb") as agg_2 FROM "series_post" LEFT OUTER JOIN "teams" ON "series_post"."tmIDWinner" = "teams"."tmID" LEFT OUTER JOIN "players_teams" ON "teams"."tmID" = "players_teams"."tmID"  WHERE "series_post"."tmIDWinner" BETWEEN 1402 AND 3535 AND "players_teams"."PF" <= 12 AND ("series_post"."lgIDWinner" LIKE '%NBA%' OR "series_post"."lgIDLoser" != 'ABA') AND "series_post"."series" IN ('D', 'A', 'O', 'C', 'L', 'N', 'M', 'K') AND "players_teams"."year" BETWEEN 1973 AND 1985 AND "series_post"."lgIDWinner" LIKE '%NB%A%'