SELECT COUNT(*) as agg_0, SUM("player_allstar"."games_played" + "player_allstar"."fg_made") as agg_1 FROM "player_allstar" LEFT OUTER JOIN "players" ON "player_allstar"."playerID" = "players"."playerID"  WHERE "players"."highSchool" LIKE '%Pre%p%' AND "players"."nameNick" NOT LIKE '%Mike%' AND ("players"."nameNick" LIKE '%Big%' OR "players"."hsState" = 'NC/NC/NJ/NC3' OR "players"."nameNick" LIKE '%Bi%g%') AND ("player_allstar"."minutes" <= 17 OR ("player_allstar"."minutes" BETWEEN 17 AND 22 AND "player_allstar"."minutes" != 27))