SELECT AVG("teams"."attendance") as agg_0, COUNT(*) as agg_1 FROM "coaches" LEFT OUTER JOIN "awards_coaches" ON "coaches"."coachID" = "awards_coaches"."coachID" LEFT OUTER JOIN "teams" ON "coaches"."tmID" = "teams"."tmID" LEFT OUTER JOIN "draft" ON "teams"."tmID" = "draft"."tmID"  WHERE "teams"."playoff" NOT LIKE '%C1%' AND "coaches"."coachID" IN ('brownla01', 'karlge01', 'fitchbi01', 'adelmri01', 'wilkele01') AND "awards_coaches"."coachID" NOT LIKE '%nelsodo01%' AND "awards_coaches"."lgID" NOT LIKE '%A%BA%' AND "awards_coaches"."award" NOT LIKE '%ABA%' AND "awards_coaches"."lgID" NOT LIKE '%NBA%'