SELECT MAX("player_allstar"."three_attempted" + "player_allstar"."three_made") as agg_0, MIN("player_allstar"."ft_made" + "players"."height") as agg_1 FROM "player_allstar" LEFT OUTER JOIN "players" ON "player_allstar"."playerID" = "players"."playerID" LEFT OUTER JOIN "awards_players" ON "players"."playerID" = "awards_players"."playerID"  WHERE "player_allstar"."conference" LIKE '%West%' AND "player_allstar"."last_name" != 'Netolicky' AND "player_allstar"."d_rebounds" <= 4.45979738634275 AND "players"."hsCountry" LIKE '%US%A%'