SELECT SUM("teams"."divWon") as agg_0, MIN("players_teams"."PostGS" + "players_teams"."oRebounds") as agg_1 FROM "players_teams" LEFT OUTER JOIN "teams" ON "players_teams"."tmID" = "teams"."tmID" LEFT OUTER JOIN "coaches" ON "teams"."tmID" = "coaches"."tmID" LEFT OUTER JOIN "awards_coaches" ON "coaches"."coachID" = "awards_coaches"."coachID" LEFT OUTER JOIN "players" ON "players_teams"."playerID" = "players"."playerID"  WHERE "awards_coaches"."lgID" != 'NBA' AND "coaches"."year" >= 1989 AND "players_teams"."id" >= 17620 AND ("coaches"."stint" BETWEEN 1 AND 2 OR ("coaches"."lgID" = 'NPBL' AND "coaches"."stint" BETWEEN 1 AND 2))