SELECT MIN("teams"."o_oreb" + "teams"."o_fga") as agg_0, MIN("players"."firstseason") as agg_1, COUNT(*) as agg_2 FROM "players_teams" LEFT OUTER JOIN "teams" ON "players_teams"."tmID" = "teams"."tmID" LEFT OUTER JOIN "players" ON "players_teams"."playerID" = "players"."playerID" LEFT OUTER JOIN "coaches" ON "teams"."tmID" = "coaches"."tmID" LEFT OUTER JOIN "series_post" ON "teams"."tmID" = "series_post"."tmIDLoser"  WHERE "players"."birthCountry" IN ('GER', 'USA', 'FRA') AND "players_teams"."PostGS" <= 0 AND "players"."useFirst" IN ('Chris') AND ("series_post"."lgIDLoser" LIKE '%NBA%' OR ("series_post"."lgIDLoser" IN ('ABA') AND "series_post"."lgIDLoser" IN ('NBA'))) AND "players_teams"."lgID" LIKE '%NBL%' AND "series_post"."lgIDLoser" NOT LIKE '%NBA%'