SELECT COUNT(*) as agg_0, SUM("teams"."d_pts") as agg_1 FROM "coaches" LEFT OUTER JOIN "teams" ON "coaches"."tmID" = "teams"."tmID" LEFT OUTER JOIN "draft" ON "teams"."tmID" = "draft"."tmID"  WHERE "teams"."o_ftm" >= 1851 AND "coaches"."stint" = 1 AND "coaches"."coachID" = 'bristal01' AND "teams"."bbtmID" NOT LIKE '%WAS%' AND "coaches"."coachID" LIKE '%fitzsco01%' AND "teams"."lgID" = 'NBA'