SELECT SUM("teams"."lost") as agg_0, MAX("series_post"."W") as agg_1, COUNT(*) as agg_2 FROM "teams" LEFT OUTER JOIN "draft" ON "teams"."tmID" = "draft"."tmID" LEFT OUTER JOIN "series_post" ON "teams"."tmID" = "series_post"."tmIDLoser" LEFT OUTER JOIN "players_teams" ON "teams"."tmID" = "players_teams"."tmID"  WHERE "draft"."lastName" LIKE '%Jo%nes%' AND "players_teams"."PostoRebounds" BETWEEN 0 AND 100 AND ("series_post"."id" BETWEEN 205 AND 723 OR "series_post"."lgIDWinner" LIKE '%ABA%' OR "series_post"."id" <= 369)