SELECT COUNT(*) as agg_0 FROM "awards_coaches" LEFT OUTER JOIN "coaches" ON "awards_coaches"."coachID" = "coaches"."coachID"  WHERE "coaches"."coachID" IN ('sloanje01', 'nelsodo01', 'mottadi01', 'fitchbi01', 'shuege01', 'wilkele01', 'adelmri01') AND "coaches"."lgID" != 'ABA' AND "coaches"."lost" IS NULL AND "awards_coaches"."lgID" IN ('ABA') AND ("coaches"."lgID" = 'PBLA' OR "coaches"."year" BETWEEN 1947 AND 2000 OR "coaches"."lgID" NOT LIKE '%ABA%')