SELECT COUNT(*) as agg_0, SUM("teams"."rank" + "players_teams"."PostSteals") as agg_1 FROM "teams" LEFT OUTER JOIN "players_teams" ON "teams"."tmID" = "players_teams"."tmID" LEFT OUTER JOIN "players" ON "players_teams"."playerID" = "players"."playerID" LEFT OUTER JOIN "series_post" ON "teams"."tmID" = "series_post"."tmIDWinner" LEFT OUTER JOIN "draft" ON "teams"."tmID" = "draft"."tmID"  WHERE "players_teams"."PostRebounds" BETWEEN 0 AND 16 AND "teams"."d_stl" <= 653