SELECT SUM("teams"."won") as agg_0 FROM "awards_coaches" LEFT OUTER JOIN "coaches" ON "awards_coaches"."coachID" = "coaches"."coachID" LEFT OUTER JOIN "teams" ON "coaches"."tmID" = "teams"."tmID"  WHERE "teams"."name" NOT LIKE '%Washingto%n%' AND "awards_coaches"."coachID" NOT LIKE '%kerrjo01%' AND "coaches"."lost" IS NOT NULL AND "coaches"."coachID" LIKE '%fitzsco01%' AND "teams"."lgID" != 'NPBL' AND "teams"."o_ftm" <= 1726