SELECT MAX("awards_coaches"."year") as agg_0 FROM "awards_coaches"  WHERE ("awards_coaches"."lgID" LIKE '%A%BA%' OR "awards_coaches"."lgID" = 'ABA') AND ("awards_coaches"."year" <= 1974 OR "awards_coaches"."id" <= 32 OR "awards_coaches"."year" = 1977) AND "awards_coaches"."coachID" = 'birdla01' AND "awards_coaches"."note" = 'tie' AND "awards_coaches"."award" != 'ABA Coach of the Year'