SELECT COUNT(*) as agg_0, COUNT(*) as agg_1, COUNT(*) as agg_2 FROM "coaches" LEFT OUTER JOIN "teams" ON "coaches"."tmID" = "teams"."tmID"  WHERE "coaches"."coachID" NOT LIKE '%adelm%ri01%' AND ("coaches"."lgID" IN ('NBA', 'ABL1', 'PBLA') OR ("coaches"."lgID" IN ('NPBL', 'ABL1') AND "coaches"."lgID" NOT LIKE '%NBA%')) AND ("teams"."lgID" IN ('PBLA') OR "teams"."franchID" LIKE '%PO%R%') AND "teams"."d_tmRebound" <= 0 AND "teams"."confID" LIKE '%WC%'