SELECT SUM("teams"."o_tmRebound") as agg_0, COUNT(*) as agg_1, AVG("teams"."o_fgm") as agg_2 FROM "draft" LEFT OUTER JOIN "teams" ON "draft"."tmID" = "teams"."tmID" LEFT OUTER JOIN "series_post" ON "teams"."tmID" = "series_post"."tmIDLoser" LEFT OUTER JOIN "coaches" ON "teams"."tmID" = "coaches"."tmID" LEFT OUTER JOIN "awards_coaches" ON "coaches"."coachID" = "awards_coaches"."coachID"  WHERE "teams"."o_asts" >= 1172 AND "coaches"."post_wins" >= 0.0 AND "teams"."homeWon" >= 18 AND "teams"."o_tmRebound" >= 0