SELECT MAX("player_allstar"."personal_fouls") as agg_0, AVG("player_allstar"."three_attempted") as agg_1 FROM "player_allstar" LEFT OUTER JOIN "players" ON "player_allstar"."playerID" = "players"."playerID"  WHERE "players"."pos" NOT LIKE '%C-F%' AND "players"."birthCountry" NOT LIKE '%US%A%' AND "players"."birthDate" NOT LIKE '%0000-00-00%' AND ("players"."birthCity" LIKE '%Ne%w%' OR ("players"."birthCity" IN ('Louisville', 'Los Angeles') AND "players"."birthCity" NOT LIKE '%C%hica%'))