SELECT MAX("series_post"."id" + "teams"."d_reb") as agg_0 FROM "teams" LEFT OUTER JOIN "series_post" ON "teams"."tmID" = "series_post"."tmIDLoser" LEFT OUTER JOIN "draft" ON "teams"."tmID" = "draft"."tmID" LEFT OUTER JOIN "coaches" ON "teams"."tmID" = "coaches"."tmID"  WHERE "series_post"."lgIDWinner" LIKE '%NBA%' AND "draft"."playerID" IS NULL AND ("teams"."confLoss" >= 12 OR ("teams"."lost" != 2 AND "teams"."confLoss" <= 0)) AND "draft"."lgID" LIKE '%A%BA%' AND "draft"."draftFrom" LIKE '%S%an%'