SELECT MAX("player_allstar"."playerID" + "player_allstar"."fg_attempted") as agg_0, MAX("player_allstar"."blocks") as agg_1 FROM "player_allstar" LEFT OUTER JOIN "players" ON "player_allstar"."playerID" = "players"."playerID"  WHERE "players"."weight" BETWEEN 182.1169712229733 AND 211.63537157344626 AND "player_allstar"."last_name" != 'Jamison' AND "players"."useFirst" IN ('Bob', 'Larry', 'John', 'Bill', 'Jim', 'Mike') AND "player_allstar"."first_name" NOT LIKE '%Mic%hael%' AND "player_allstar"."ft_made" BETWEEN 0.0 AND 4.342141171391973 AND "players"."collegeOther" IN ('Indiana', 'Southern Idaho', 'Illinois', 'Vanderbilt', 'Notre Dame', 'Kentucky', 'Vincennes', 'Minnesota', 'San Jacinto JC')