SELECT SUM("awards_players"."year" + "awards_players"."playerID") as agg_0, MIN("players"."playerID") as agg_1, COUNT(*) as agg_2 FROM "awards_players" LEFT OUTER JOIN "players" ON "awards_players"."playerID" = "players"."playerID"  WHERE "players"."highSchool" LIKE '%W%est%' AND "players"."pos" LIKE '%G%-F%' AND "awards_players"."pos" IN ('F', 'G', 'F/C', 'F/G') AND "players"."birthCity" NOT LIKE '%Ne%w%'