SELECT MAX("teams"."o_asts" + "teams"."d_fgm") as agg_0 FROM "awards_coaches" LEFT OUTER JOIN "coaches" ON "awards_coaches"."coachID" = "coaches"."coachID" LEFT OUTER JOIN "teams" ON "coaches"."tmID" = "teams"."tmID"  WHERE "teams"."d_pts" BETWEEN 2046 AND 4177 AND "coaches"."coachID" LIKE '%rileypa01%' AND "awards_coaches"."year" <= 1983 AND ("coaches"."year" != 1995 OR "coaches"."stint" BETWEEN 1 AND 3 OR "coaches"."year" <= 1972)